import WeatherCard from "./WeatherCard";
import "bulma/css/bulma.min.css"

const monday = mondaySdk();
monday.setToken("eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjE2MDg0Njc5MCwidWlkIjozMDMxOTI1MCwiaWFkIjoiMjAyMi0wNS0xN1QwMToxNzowNC4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6NzcxNzE2NywicmduIjoidXNlMSJ9.X7R9ymORVX0CcbbBotMLdk_VjPmjfBWszsrkAvhUa3s")

class App extends React.Component {
  constructor(props) {
    super(props);
    // Default state
    this.state = {
      cities: [],
      setData: {},
      settings: {},
      myData: { boards: [] }
    };

  }

  componentDidMount() {   
    const getWeather = async() =>
      { let res = await monday.api('query { boards( ids : 2664704591 ) { items { id : name column_values { text }}}}');
          res.data.boards.map(async (board) => {

                        let res = board.items.forEach( async (item) => {
                          console.log(item)
                          let json = await fetch(`http://api.weatherapi.com/v1/current.json?key=c156f7f2a3d84ae0861121630223006&q=${item.column_values[3].text}&aqi=no`)
                            json = await json.json();
                            console.log(json);
                              let city={                            
                                name: json.location.name,
                                localtime: json.location.localtime,
                                temperaturaC:json.current.temp_c,
                                temperaturaF:json.current.temp_f,
                                weatherIcon:json.current.condition.icon
                          };
                          let cities =[...this.state.cities, city]
                          this.setState({cities});
                          console.log(cities)
                        })
          });
      };

    getWeather();
  }

  render() {    
    const { myData } = this.state;   
    const { cities } = this.state;
    return (
      <div className="App">
        {
          cities.length === 0? (<h3>Loading . . .</h3> ) :
           cities.map((el) => (
            <WeatherCard name={el.name} temp_c={el.temp_c} temp_f={el.temp_f}
                        icon={el.weatherIcon} localtime={el.localtime}/>
           ))
        }        
        <div className="btn btn-primary"> hola mi gente</div>        
        <div>

        {/*          
        {myData.boards.map( (board) => {
          board.items.map((item) => cities.push(item.column_values[3].text) )})}
         */}
        </div>
      </div>
    );
  }
}

export default App;
